-- VHDL Configurator
-- 1-Bit Full Subtractor 

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <INPUT_BIT1> : in STD_LOGIC;
	<INPUT_BIT2> : in STD_LOGIC;
	<INPUT_BORROW_BIT> : in STD_LOGIC;
	--
	<DIFFERENCE_BIT> : out STD_LOGIC;
	<BORROW_BIT> : out STD_LOGIC);
end <NAME>;

architecture Behavioral of <NAME> is

begin

	<NAME>_PROC: process(<INPUT_BIT1>, <INPUT_BIT2>, <INPUT_BORROW_BIT>)
	begin
		if <INPUT_BIT1> = '0' then
			if <INPUT_BIT2> = '1' or <INPUT_BORROW_BIT> = '1' then
				<BORROW_BIT> <= '1';
				<DIFFERENCE_BIT> <= '1';
				if <INPUT_BIT2> = '1' and <INPUT_BORROW_BIT> = '1' then 
					<DIFFERENCE_BIT> <= '0';
				end if;
			else
				<BORROW_BIT> <= '0';
				<DIFFERENCE_BIT> <= '0';
			end if;
		else
			if <INPUT_BIT2> = '1' and <INPUT_BORROW_BIT> = '1' then
				<BORROW_BIT> <= '1';
				<DIFFERENCE_BIT> <= '1';
			else
				if <INPUT_BIT2> = '0' and <INPUT_BORROW_BIT> = '0' then
					<BORROW_BIT> <= '0';
					<DIFFERENCE_BIT> <= '1';
				else
					<BORROW_BIT> <= '0';
					<DIFFERENCE_BIT> <= '0';
				end if;
			end if;
		end if;
	end process;
end Behavioral;
