-- NOVA VHDL CONFIGURATOR
-- 1-Bit Half Subtractor (Adapted from: https://buzztech.in/vhdl-code-half-adder-structural-behavioral-dataflow/)

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <INPUT_BIT1> : in STD_LOGIC;
	<INPUT_BIT2> : in STD_LOGIC;
	--
	<DIFFERENCE_BIT> : out STD_LOGIC;
	<BORROW_BIT> : out STD_LOGIC);
end <NAME>;

architecture Behavioral of <NAME> is

begin

	<NAME>_PROC: process(<INPUT_BIT1>, <INPUT_BIT2>)
	begin
		if <INPUT_BIT1> = '1' then
			<DIFFERENCE_BIT> <= not <INPUT_BIT2>;
			<BORROW_BIT> <= '0';
		else
			if <INPUT_BIT2> = '1' then
				<DIFFERENCE_BIT> <= '1';
				<BORROW_BIT> <= '1';
			else
				<DIFFERENCE_BIT> <= '0';
				<BORROW_BIT> <= '0';
			end if;
		end if;
	end process;

end Behavioral;
