-- SIPO Shift Register (Adapted from: https://allaboutfpga.com/vhdl-code-for-4-bit-shift-register/)

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME>_SIPO_Shift_Register is
	Port ( <CLOCK> : in STD_LOGIC;
	<RESET> : in STD_LOGIC;
	<NAME> : in STD_LOGIC;
	--
	<OUTPUT> : out STD_LOGIC_VECTOR (<NUMBER_OF_BITS> downto 0));
end <NAME>_SIPO_Shift_Register;

architecture Behavioral of <NAME>_SIPO_Shift_Register is 

signal Q: STD_LOGIC_VECTOR(<NUMBER_OF_BITS> downto 0);

begin

	SIPO_REGISTER: process(<CLOCK>)
	begin
		if <EDGE_TYPE>(<CLOCK>) then
			if <RESET> = '1' then
				Q_signal <= (others => '0');
			else
				Q_signal((<NUMBER_OF_BITS_MINUS_1>) downto (<MIDDLE_BIT_MINUS_1>)) <= Q_signal((<MIDDLE_BIT>) downto 0);
				Q_signal(0) <= <INPUT>;
			end if;
		end if;
	end process;
end Behavioral;

