-- NOVA VHDL CONFIGURATOR
-- Convert from Integer to STD_LOGIC_VECTOR using Numeric_Std (Adapted from: https://www.nandland.com/vhdl/tips/tip-convert-numeric-std-logic-vector-to-integer.html)

signal <INPUT>  : INTEGER;
signal <OUTPUT> : STD_LOGIC_VECTOR(<NUMBER_OF_BITS_MINUS_1> downto 0);

-- This line demonstrates how to convert positive integers
<OUTPUT> <= STD_LOGIC_VECTOR(to_unsigned(<INPUT>, <OUTPUT>'length));

-- This line demonstrates how to convert positive or negative integers
<OUTPUT> <= STD_LOGIC_VECTOR(to_signed(<INPUT>, <OUTPUT>'length));
