-- NOVA VHDL CONFIGURATOR
-- Convert from Integer to Unsigned using Numeric_Std (Adapted from: https://www.nandland.com/vhdl/tips/tip-convert-numeric-std-logic-vector-to-integer.html)

signal <INPUT>  : INTEGER;
signal <OUTPUT> : UNSIGNED(<NUMBER_OF_BITS_MINUS_1> downto 0);

<OUTPUT> <= to_unsigned(<INPUT>, <OUTPUT>'length);
