-- NOVA VHDL CONFIGURATOR
-- Convert from Signed to Integer using Numeric_Std (Adapted from: https://www.nandland.com/vhdl/tips/tip-convert-numeric-std-logic-vector-to-integer.html)

signal <INPUT>  : SIGNED(<NUMBER_OF_BITS_MINUS_1> downto 0);
signal <OUTPUT> : INTEGER;

<OUTPUT> <= to_integer(<INPUT>);
