-- VHDL Configurator
-- Convert from Std_Logic_Vector to Integer using Numeric_Std 

signal <INPUT>  : STD_LOGIC_VECTOR(<NUMBER_OF_BITS_MINUS_1> downto 0);
signal <OUTPUT> : INTEGER;

-- This line demonstrates the unsigned case
<OUTPUT> <= to_integer(UNSIGNED(<INPUT>));

-- This line demonstrates the signed case
<OUTPUT> <= to_integer(SIGNED(<INPUT>));
