-- NOVA VHDL CONFIGURATOR
-- Convert from Unsigned to Signed using Numeric_Std (Adapted from: https://www.nandland.com/vhdl/tips/tip-convert-numeric-std-logic-vector-to-integer.html)

signal <INPUT>  : UNSIGNED(<NUMBER_OF_BITS_MINUS_1> downto 0);
signal <OUTPUT> : SIGNED(<NUMBER_OF_BITS_MINUS_1> downto 0);

<OUTPUT> <= SIGNED(<INPUT>);
