-- NOVA VHDL CONFIGURATOR
-- Convert from Unsigned to Std_Logic_Vector using Numeric_Std (Adapted from: https://www.nandland.com/vhdl/tips/tip-convert-numeric-std-logic-vector-to-integer.html)

signal <INPUT>  : UNSIGNED(<NUMBER_OF_BITS_MINUS_1> downto 0);
signal <OUTPUT> : STD_LOGIC_VECTOR(<NUMBER_OF_BITS_MINUS_1> downto 0);

<OUTPUT> <= STD_LOGIC_VECTOR(<INPUT>);
