library IEEE;
use IEEE.STD_LOGIC_1164.ALL;
use IEEE.NUMERIC_STD.ALL;

entity <NAME>_Down_Counter is
	Port ( Clock : in STD_LOGIC;
	Reset : in STD_LOGIC;
	<OUTPUT> : out STD_LOGIC_VECTOR (<NUMBER_OF_BITS> downto 0);
end <NAME>_Down_Counter;

architecture Behavioral of <NAME>_Down_Counter is
	signal temp_signal : STD_LOGIC_VECTOR(<NUMBER_OF_BITS> downto 0);
begin
	<NAME>_Down_Counter: process (Clock, Reset)
	begin
		if(Reset = '1') then
			temp_signal <= (others => '1');
		elsif (<EDGE_TYPE>(Clock)) then
			temp_signal <= STD_LOGIC_VECTOR(unsigned(temp_signal) - 1);
		end if;
	end process;
	<OUTPUT> <= temp_signal;
end Behavioral;
