-- NOVA VHDL CONFIGURATOR
-- Counter UP Synchronous (Adapted from: https://www.tutorialspoint.com/vlsi_design/vhdl_programming_for_sequential_circuits.htm)

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;
use IEEE.NUMERIC_STD.ALL;

entity <NAME> is
	Port ( Clock : in STD_LOGIC;
	Reset : in STD_LOGIC;
	<OUTPUT> : out STD_LOGIC_VECTOR (<NUMBER_OF_BITS_MINUS_1> downto 0));
end <NAME>;

architecture Behavioral of <NAME> is
	signal temp_signal : STD_LOGIC_VECTOR(<NUMBER_OF_BITS_MINUS_1> downto 0);
begin
	<NAME>: process (Clock, Reset)
	begin
		if(Reset = '1') then
			temp_signal <= (others => '0');
		elsif (<EDGE_TYPE>(Clock)) then
			temp_signal <= STD_LOGIC_VECTOR(unsigned(temp_signal) + 1);
		end if;
	end process;
	<OUTPUT> <= temp_signal;
end Behavioral;
