moduleCode:
-- NOVA VHDL CONFIGURATOR
-- ENCODER (Adapted from: https://allaboutfpga.com/vhdl-code-for-4-to-2-encoder/)

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <INPUT> : in STD_LOGIC_VECTOR (<NUMBER_OF_BITS> downto 0);
	<OUTPUT> : out STD_LOGIC_VECTOR (<SELECT_NUMBER_OF_BITS> downto 0));
end <NAME>;

architecture Behavioral of <NAME> is

begin

	<NAME>_ENCODER_PROC: process(<INPUT>)
	begin
		case <INPUT> is 
			when "<NUMBER_OF_BITS_BINARY>" => <OUTPUT> <= "<SELECT_NUMBER_OF_BITS_BINARY>";
			<AUTO_GENERATE>
			when others => <OUTPUT> <= "ZZ";
		end case;
	end process;

end Behavioral;
