-- NOVA VHDL CONFIGURATOR
-- Flip-Flop Type SR (Adapted from: https://www.tutorialspoint.com/vlsi_design/vhdl_programming_for_sequential_circuits.htm)

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( Set : in STD_LOGIC;
	Reset : in STD_LOGIC;
	clk : in STD_LOGIC;
	Q_Signal : out STD_LOGIC);
end <NAME>_SR_flipflop;

architecture Behavioral of <NAME> is

begin
	SR_flipflop: process(clk)
	begin
		if(<EDGE_TYPE>(clk)) then
			if(Set='0' and Reset='0')then

			elsif(Set='1' and Reset='1')then
				Q_Signal<='Z';
			elsif(Set='0' and Reset='1')then
				Q_Signal<='0';
			else
				Q_Signal<='1';
			end if;
		end if;
	end process; 
end Behavioral;
