library IEEE;
use IEEE.STD_LOGIC_1164.ALL;
entity <NAME>_T_flipflop is
	Port ( T : in STD_LOGIC;
		clk : in STD_LOGIC;
		Q_Signal : out STD_LOGIC);
end <NAME>_T_flipflop;

architecture Behavioral of <NAME>_T_flipflop is

begin
		T_flipflop: process (clk)
			variable temp_sig: STD_LOGIC := '0';
		begin
			if <EDGE_TYPE>(clk) then
				if T='0' then
					temp_sig := temp_sig;
				elsif T='1' then
					temp_sig := not (temp_sig);
				end if;
			end if;
			Q_Signal <= temp_sig;
		end process;
end Behavioral;
