-- NOVA VHDL CONFIGURATOR
-- High Level Adder and/or Subtractor (Adapted from: https://www.allaboutcircuits.com/technical-articles/review-of-vhdl-signed-unsigned-data-types/)

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <INPUT1> : in <ARITHMETIC_DATA_TYPE>(<NUMBER_OF_BITS_MINUS_1> downto 0);
	<INPUT2> : in <ARITHMETIC_DATA_TYPE>(<NUMBER_OF_BITS_MINUS_1> downto 0);
	--
	<OUTPUT> : out <ARITHMETIC_DATA_TYPE>(<NUMBER_OF_BITS_MINUS_1> downto 0));
end <NAME>;

architecture Behavioral of <NAME> is

begin

	<OUTPUT> <= <INPUT1> + <INPUT2>;

end Behavioral;
