-- NOVA VHDL CONFIGURATOR
-- High Level Adder and/or Subtractor

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <INPUT1> : in INTEGER;
	<INPUT2> : in INTEGER;
	--
	<OUTPUT> : out INTEGER);
end <NAME>;

architecture Behavioral of <NAME> is

begin

	<OUTPUT> <= <INPUT1> + <INPUT2>;

end Behavioral;
