-- NOVA VHDL CONFIGURATOR
-- Multiplexer (Adapted from: https://startingelectronics.org/software/VHDL-CPLD-course/tut4-multiplexers/)

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <INPUT> : in STD_LOGIC;
	<SELECT> : in STD_LOGIC_VECTOR (<SELECT_NUMBER_OF_BITS> downto 0);
	--
	<OUTPUT> : out STD_LOGIC_VECTOR (<NUMBER_OF_BITS_MINUS_1> downto 0));
end <NAME>;

architecture Behavioral of <NAME> is

begin

	with <SELECT> select <OUTPUT> <= 
		<INPUT>(<NUMBER_OF_BITS_MINUS_1>) when "<SELECT_NUMBER_OF_BITS_BINARY>",
		<AUTO_GENERATE>
		'0'  when others;
end Behavioral;
