library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <INPUT> : in STD_LOGIC;
	<SELECT> : in STD_LOGIC_VECTOR (<SELECT_NUMBER_OF_BITS> downto 0);
	--
	<OUTPUT> : out STD_LOGIC_VECTOR (<NUMBER_OF_BITS> downto 0));
end <NAME>;

architecture Behavioral of <NAME> is

begin

	MULTIPLEXER_PROCESS: process(<SELECT>, <INPUT>)
	begin
		case <SELECT> is
			when <SELECT_NUMBER_OF_BITS_BINARY> => <OUTPUT>(<NUMBER_OF_BITS>) <= <INPUT>;
			<AUTO_GENERATE>
			when <SELECT_NUMBER_OF_BITS_BINARY> => <OUTPUT>(0) <= <INPUT>;
			when others => <OUTPUT> <= 'X';
		end case;
	end process;

end Behavioral;
