-- NOVA-HDL-Assist 
-- N-Bit Adder 

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME>_adder_n_bits is
	Port ( <INPUT_BIT1> : in STD_LOGIC_VECTOR (3 downto 0);
		<INPUT_BIT2> : in STD_LOGIC_VECTOR (3 downto 0);
		--
		<SUM_BIT> : out STD_LOGIC_VECTOR (3 downto 0));
end <NAME>_adder_n_bits;

architecture Behavioral of <NAME>_adder_n_bits is
	signal c0,c1,c2,c3 : STD_LOGIC;

begin

	Half_Adder: entity work.half_adder port map (<INPUT_BIT1> => <INPUT_BIT1>(0), <INPUT_BIT2> => <INPUT_BIT2>(0), <CARRY_BIT> => c0, <SUM_BIT> => <SUM_BIT>(0));

	Full_Adder0: entity work.full_adder port map (<INPUT_BIT1> => <INPUT_BIT1>(1), <INPUT_BIT2> => <INPUT_BIT2>(1), <INPUT_CARRY_BIT> => c0, <CARRY_BIT> => c1, <SUM_BIT> => <SUM_BIT>(1));

	Full_Adder1: entity work.full_adder port map (<INPUT_BIT1> => <INPUT_BIT1>(2), <INPUT_BIT2> => <INPUT_BIT2>(2), <INPUT_CARRY_BIT> => c1, <CARRY_BIT> => c2, <SUM_BIT> => <SUM_BIT>(2));

	Full_Adder2: entity work.full_adder port map (<INPUT_BIT1> => <INPUT_BIT1>(3), <INPUT_BIT2> => <INPUT_BIT2>(3), <INPUT_CARRY_BIT> => c2, <CARRY_BIT> => c3, <SUM_BIT> => <SUM_BIT>(3));

end Behavioral;
