-- NOVA-HDL-Assist
-- N-Bit Full Subtractor 

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;  

entity <NAME> is
Port ( <INPUT_BIT1> : in STD_LOGIC_VECTOR (3 downto 0); 
		<INPUT_BIT2> : in STD_LOGIC_VECTOR (3 downto 0);
		--
		<DIFFERENCE_BIT> : out STD_LOGIC_VECTOR (3 downto 0));
end <NAME>;

architecture Behavioral of <NAME> is
	signal c0,c1,c2,c3 : STD_LOGIC;

begin

	Half_Subtractor: entity work.half_subtractor port map (<INPUT_BIT1> => <INPUT_BIT1>(0), <INPUT_BIT2> => <INPUT_BIT2>(0), <DIFFERENCE_BIT> => c0, <DIFFERENCE_BIT> => <DIFFERENCE_BIT>(0));

	Full_Subtractor0: entity work.full_subtractor port map (<INPUT_BIT1> => <INPUT_BIT1>(1), <INPUT_BIT2> => <INPUT_BIT2>(1), <INPUT_BORROW_BIT> => c0, <BORROW_BIT> => c1, <DIFFERENCE_BIT> => <DIFFERENCE_BIT>(1));

	Full_Subtractor1: entity work.full_subtractor port map (<INPUT_BIT1> => <INPUT_BIT1>(2), <INPUT_BIT2> => <INPUT_BIT2>(2), <INPUT_BORROW_BIT> => c1, <BORROW_BIT> => c2, <DIFFERENCE_BIT> => <DIFFERENCE_BIT>(2));

	Full_Subtractor2: entity work.full_subtractor port map (<INPUT_BIT1> => <INPUT_BIT1>(3), <INPUT_BIT2> => <INPUT_BIT2>(3), <INPUT_BORROW_BIT> => c2, <BORROW_BIT> => c3, <DIFFERENCE_BIT> => <DIFFERENCE_BIT>(3));
end Behavioral;
