architecture Behavioral of <NAME> is

signal cont : integer range 0 to (<FREQ_HZ>/<FOSC_HZ>-1) := 0;

begin

	process(<CLK>,<RESET>)
	begin
	if <RESET> = 1 then
				cont <= 0;
				<OUTPUT> <= 0;
				else
	if <EDGE_TYPE>(<CLK>) then
		if cont = ((<FREQ_HZ>/<FOSC_HZ>)-1) then
				cont 	 <= 0;
				<OUTPUT> <= 0;
		else
			if cont < (((<FREQ_HZ>/<FOSC_HZ>)*<PWM_PERCENT>)/100) then
					<OUTPUT> <= 1;
					cont <= cont + 1;	
					else
					<OUTPUT> <= 0;
					cont <= cont + 1;
			end if;
		end if;
	end if;
	end if;

	end process;


end Behavioral;
