moduleCode:
library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is

Port
(
	<CLOCK> : in STD_LOGIC;
	<RESET> : in STD_LOGIC;
	<OUTPUT> : out STD_LOGIC := 0
);


end <NAME>;

architecture Behavioral of <NAME> is

	signal cont : integer range 0 to (<FREQ_HZ>/<FOSC_HZ>-1) := 0;

begin

	process(<CLOCK>)
	begin		
		if <EDGE_TYPE>(<CLK>) then
			if <RESET> = '1' then
				cont <= 0;
				<OUTPUT> <= '0';
			else
				if cont = ((<FREQ_HZ>/<FOSC_HZ>)-1) then
					cont <= 0;
					<OUTPUT> <= '0';
				else
					if cont < (((<FREQ_HZ>/<FOSC_HZ>)*<PWM_PERCENT>)/100) then
						<OUTPUT> <= '1';
						cont <= cont + 1;
					else
						<OUTPUT> <= '0';
						cont <= cont + 1;
					end if;
				end if;
			end if;
		end if;
	end process;
end Behavioral;
