-- NOVA VHDL CONFIGURATOR
-- State Machine 

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <INPUT>: in STD_LOGIC;
	reset: in STD_LOGIC;
	clock: in STD_LOGIC;
	--
	<OUTPUT>: in STD_LOGIC);
end <NAME>;

architecture Behavioral of <NAME> is
	type state_type is ( <NUMBER_OF_STATES>);
	signal current_state, next_state: state_type;
	<AUTO_GENERATE_PROCEDURES>

begin

	CLOCK_PROCESS_SYNC: process(clock)
	begin
		if <EDGE_TYPE>(clock) then
			if reset = '1' then
				current_state <= <RESET_STATE_NAME>;
			else
				current_state <= next_state;
			end if;
		end if;
	end process;

	NEXT_STATE_PROCESS: process (current_state, input)
	begin
		next_state<=current_state;
		case current_state is
			<AUTO_GENERATE>
		end case;
	end process;

	OUTPUT_PROCESS: process( )
	begin

		-- <OUTPUT><='0';

		-- <OUTPUT><='1';

	end process;
end Behavioral;
